import React, { useState } from "react";
import { Tab } from "./Tab";
import styles from "./Tabs.module.css";

export const Tabs = (props) => {
  const [tabIndexClicked, setTabIndexClicked] = useState("All");
  return (
    <div className={styles["tabs__wrap"]}>
      <Tab
        onClick={(e) => {
          setTabIndexClicked(e.target.id);
        }}
        text={"All"}
        id="All"
        className={`${tabIndexClicked === "All" ? styles.active : ""} ${
          styles["tab-item"]
        }`}
      />
      <Tab
        onClick={(e) => {
          setTabIndexClicked(e.target.id);
        }}
        text={"Human res"}
        id="Hrs"
        className={`${tabIndexClicked === "Hrs" ? styles.active : ""} ${
          styles["tab-item"]
        }`}
      />
      <Tab
        t
        onClick={(e) => {
          setTabIndexClicked(e.target.id);
        }}
        text={"Designers"}
        id="Designers"
        className={`${tabIndexClicked === "Designers" ? styles.active : ""} ${
          styles["tab-item"]
        }`}
      />
      <Tab
        t
        onClick={(e) => {
          setTabIndexClicked(e.target.id);
        }}
        text={"Developers"}
        id="Developers"
        className={`${props.tabIndex === "Developers" ? styles.active : ""} ${
          styles["tab-item"]
        }`}
      />
    </div>
  );
};
