import React, { useState } from "react";
import styles from "./FilterButtons.module.css";

export const FilterButtons = (props) => {
  const [filterButtonSelected, setFilterButtonSelected] = useState("All");
  let selectedButton = "";
  
  const clickHandler = (e) => {
    if (e.target.id === "All") {
        console.log(e.target.id)
        selectedButton = `selected-filter__button`;

    } 

    setFilterButtonSelected((prevState) => filterButtonSelected != prevState);
  };
  return (
    <div className={styles["filter-buttons__wrap"]}>
      <div onClick={clickHandler} id="All"className={styles[selectedButton]}>
        All
      </div>
      <div id="IOS dev-s">IOS dev-s</div>
      <div id="Designers">Designers</div>
    </div>
  );
};
