        name: obj.name,
        surname: obj.surname,
        gender: obj.gender,
        birthday: obj.birthDay,
        salary: obj.salary,
        department: obj.department,
        id: Math.random().toString(),
      });
      console.log(updatedList);
      return updatedList;
    });
  };

  return (
    <>
