import { useState, useRef } from "react";
import { Worker } from "../Worker/Worker";
import { ListHeader } from "../ListHeader/ListHeader";
import { Tabs } from "../Tabs/Tabs";
import { Search } from "../SearchField/Search";
import styles from "./WorkersList.module.css";

const WorkersList = (props) => {
  const [tabIndexClicked, setTabIndexClicked] = useState("All");
  const [userInputText, setUserInputText] = useState("");

  let inputValue;
  const inputRef = useRef();
  const searchWorker = () => {
    inputValue = inputRef.current.value;
    console.log(inputValue);
  };
  console.log(userInputText);

  const userInputChangeHandler = (e) => {
    setUserInputText(e.target.value);
  };

  const clearUserInput = () => {
    setUserInputText("");
  };

  const setTabIndex = (index) => {
    setTabIndexClicked(index);
  };
  const workeksList = props.list;
  let listContent;
  let estimates;

  if (tabIndexClicked === "All") {
    let totalAmount = workeksList.length;

    let totalSalaries = workeksList.reduce((total, worker) => {
      return Math.round((total + parseInt(worker.salary)) / workeksList.length);
    }, 0);

    estimates = (
      <div className={styles.estimates}>
        <div>{`Average salary: ${totalSalaries}$`}</div>
        <div>{`Quantity: ${totalAmount}`}</div>
      </div>
    );

    listContent = (
      <>
        {workeksList.map((worker) => (
          <li key={worker.id}>
            <Worker
              name={worker.name}
              surname={worker.surname}
              gender={worker.gender}
              birthday={worker.birthDay}
              salary={worker.salary}
              department={worker.department}
            />
          </li>
        ))}
        <>{totalAmount && totalSalaries > 0 ? estimates : <p>No info!</p>}</>
      </>
    );
  } else {
    let totalAmount;

    let filteredList = workeksList.filter(
      (worker) => worker.department === tabIndexClicked
    );

    totalAmount = filteredList.length;
    let totalSalaries = filteredList.reduce((total, worker) => {
      return Math.round(
        (total + parseInt(worker.salary)) / filteredList.length
      );
    }, 0);

    estimates = (
      <div className={styles.estimates}>
        <div>{`Average salary: ${totalSalaries}$`}</div>
        <div>{`Quantity: ${totalAmount}`}</div>
      </div>
    );

    listContent = (
      <>
        {filteredList.map((worker) => (
          <li key={worker.id}>
            <Worker
              name={worker.name}
              surname={worker.surname}
              gender={worker.gender}
              birthday={worker.birthDay}
              salary={worker.salary}
              department={worker.department}
            />
          </li>
        ))}
        <>{totalAmount && totalSalaries > 0 ? estimates : <p>No info!</p>}</>
      </>
    );
  }

  return (
    <div className={styles["list__wrap"]}>
      <div className={styles["content-wrap"]}>
        <Search
          onInputChange={userInputChangeHandler}
          value={userInputText}
          onClearUserInput={clearUserInput}
          onSearchWorker={searchWorker}
          ref={inputRef}
        />
        <Tabs onTabIndexHandler={setTabIndex} tabIndex={tabIndexClicked} />
        <ListHeader />
        {listContent.props.children.length === 0 ? (
          <p>No workers FUCK!</p>
        ) : (
          listContent
        )}
      </div>
    </div>
  );
};

export default WorkersList;
