import React, { useState } from "react";

export const useTabs = (value) => {
  const [active, setActive] = useState(value);

  function toggle() {
    setActive((prevState) => !prevState);
  }
  function closeOtherTabs() {
    if (active) {
        setActive(false)
    }
  }


  return {
    toggle,
    active
  };
};
