import { useState, useEffect } from "react";
import WorkersList from "./components/List/WorkersList";
import AddWorkerForm from "./components/Form/AddWorkerForm";
import { Button } from "./components/Button/Button";
import { Header } from "./components/Header/Header";

const App = () => {
  const [workers, setWorkers] = useState([]);
  const [showForm, setShowForm] = useState(false);

  const clickHandler = () => {
    setShowForm((prevState) => !prevState);
  };

  const setFormClose = () => {
    setShowForm(false);
  };

  const fetchWorkers = async () => {
    const response = await fetch(
      "https://react-http-175ac-default-rtdb.firebaseio.com/workers.json"
    );
    if (!response.ok) {
      throw new Error("Request failed");
    }
    const data = await response.json();
    const loadedWorkers = [];
    for (const workerKey in data) {
      loadedWorkers.push({
        id: workerKey,
        name: data[workerKey].name,
        surname: data[workerKey].surname,
        birthDay: data[workerKey].birthDay,
        salary: data[workerKey].salary,
        department: data[workerKey].department,
        gender: data[workerKey].gender,
      });
    }
    setWorkers(loadedWorkers);
  };
  useEffect(() => {
    fetchWorkers();
  }, []);

  const workerAddHandler = (worker) => {
    setWorkers((prevWorkers) => prevWorkers.concat(worker));
    setShowForm(false)
  };

  return (
    <>
      <Header onClick={clickHandler} />
      {showForm && (
        <AddWorkerForm
          onAddWorkerHandler={workerAddHandler}
          onCloseForm={setFormClose}
        />
      )}
      <WorkersList list={workers} />
    </>
  );
};

export default App;
