import React, { useState } from "react";
import styles from "./FilterButtons.module.css";

export const FilterButtons = (props) => {
  const [filterButtonSelected, setFilterButtonSelected] = useState(false);

  const clickHandler = (e) => {
    console.log(filterButtonSelected);
    setFilterButtonSelected((prevState) => !prevState);
  };
  return (
    <div className={styles["filter-buttons__wrap"]}>
      <div
        onClick={clickHandler}
        className={`${
          !filterButtonSelected ? "" : styles["selected-filter__button"]
        }`}
      >
        {props.text}
      </div>
    </div>
  );
};
