import React, { useState } from "react";
import useInput from "../hooks/use-input";
import styles from "./AddWorkerForm.module.css";
const AddWorkerForm = () => {
  const clickHandler = (e) => {
    e.preventDefault();
  };
  const [entereadName, setEnteredName] = useState ("");
  const [enteredData, setEnteredData] = useData("");


  const inputOnChangeHadler = (e) => {
    setEnteredData (e.target.value);
  }

  return (
    <>
      <form className={styles["worker-add__form"]}>
        <div className={styles["workerks-pers__wrap"]}>
          <label htmlFor="">Name</label>
          <div className={styles["worker-pers__info"]}>
            <input type="text" placeholder="Name" id="name" value={enteredData} onChange={inputOnChangeHadler}/>
            <input type="text" placeholder="Surname" />
          </div>
        </div>
        <div className={styles["second-section__wrap"]}>
          <div className={styles["workerks-pers__wrap"]}>
            <label htmlFor="">Gender</label>
            <div className={styles["worker-pers__info"]}>
              <select name="" id="">
                <option value="">Male</option>
                <option value="">Female</option>
              </select>
            </div>
          </div>
          <div className={styles["workerks-pers__wrap"]}>
            <label htmlFor="">Birthday</label>
            <div className={styles["worker-pers__info"]}>
              <input type="date" />
            </div>
          </div>
        </div>
        <div className={styles["workerks-pers__wrap"]}>
          <label htmlFor="">Operational data</label>
          <div className={styles["worker-pers__info"]}>
            <input type="number" name="" id="" placeholder="Salary" min="300" />
            <input type="text" placeholder="Department" />
          </div>
        </div>
        <button type="submit" onClick={clickHandler}>
          Add
        </button>
      </form>
    </>
  );
};
export default AddWorkerForm;
