import { useState, useRef } from "react";
import styles from "./Seacrch.module.css";

export const Search = (props) => {
  const inputRef = useRef ();

  const handleSearch = () => {
    inputValue = inputRef.current.value
  }
  return (
    <>
      <div className={styles["search-field__wrap"]}>
        <div className={styles["search-text__wrap"]}>
          <input
            type="text"
            ref={inputRef}
          />
        </div>
        <button onClick={props.onSearchWorker}>Search</button>
        <button className={styles["search-text__clear"]} onClick={props.onClearUserInput}>Clear</button>
      </div>
    </>
  );
};
