import React, { useState } from "react";
import WorkersList from "./components/WorkersList";
import AddWorkerForm from "./components/AddWorkerForm";

const App = () => {
  const [workers, setWorkers] = useState([]);
  const expenses = [
    {
      id: "e1",
      name: "Alex",
      surname: "Ziierov",
      gender: "male",
      birthday: "1999-05-11",
      salary: "25000",
      department: "IOS dev",
    },
    {
      id: "e2",
      name: "Anstasia",
      surname: "Kovalenko",
      gender: "female",
      birthday: "2003-01-07",
      salary: "32000",
      department: "IOS dev",
    },
  ];

  const fetchWorkers = async () => {
    const response = await fetch(
      "https://react-http-175ac-default-rtdb.firebaseio.com/workers.json"
    );
    if (!response.ok) {
      throw new Error("Request failed");
    }
    const data = await response.json();
    const loadedWorkers = [];
    for (const workerKey in data) {
      loadedTasks.push({
        id: workerKey,
        name: data[taskKey].name,
        surname: data[taskKey].surname,
        birthDay: data[taskKey].birthday,
        salary: data[taskKey].salary,
        department: data[taskKey].department,
        gender: data[taskKey].gender,
      });
    }
  };

  const addWorkerHandler = (obj) => {
    setWorkers((prevList) => {
      let updatedList = [...prevList];
      updatedList.unshift({
        name: obj.name,
        surname: obj.surname,
        gender: obj.gender,
        birthday: obj.birthDay,
        salary: obj.salary,
        department: obj.department,
        id: Math.random().toString(),
      });
      console.log(updatedList);
      return updatedList;
    });
  };
  const workerAddHandler = (worker) => {
    setWorkers((prevWorkers) => prevWorkers.concat(worker));
  };

  return (
    <>
      <AddWorkerForm onAddWorkerHandler={addWorkerHandler} />
      <WorkersList list={expenses.concat(workers)} />
    </>
  );
};

export default App;
