import { useState } from "react";
import styles from "./Seacrch.module.css";

export const Search = (props) => {
  const [userInputText, setUserInputText] = useState("");

  const userInputChangeHandler = (e) => {
    setUserInputText(e.target.value);
  };

  const userInputClearHandler = () => {
    setUserInputText("");
  };
  return (
    <>
      <div className={styles["search-field__wrap"]}>
        <div className={styles["search-text__wrap"]}>
          <input
            type="text"
            onChange={userInputChangeHandler}
            value={userInputText}
          />
        </div>
        <button
          className={styles["search-text__clear"]}
          onClick={userInputClearHandler}
        >
          Clear
        </button>
      </div>
    </>
  );
};
