import React, { useState } from "react";
import useInput from "../hooks/use-input";
import styles from "./AddWorkerForm.module.css";
const AddWorkerForm = () => {
  const clickHandler = (e) => {
    e.preventDefault();
  };

  const name = useInput("");
  const surname = useInput("");
  const birthDay = useInput("");
  const salary = useInput("");
  const department = useInput("");
  const sumbitHandler = (e) => {
    e.preventDefault();
    name.onSubmit(e);
    surname.onSubmit(e);
    birthDay.onSubmit(e);
    salary.onSubmit(e);
    department.onSubmit(e);
  };

  return (
    <>
      <form className={styles["worker-add__form"]}>
        <div className={styles["workerks-pers__wrap"]}>
          <label htmlFor="">Name</label>
          <div className={styles["worker-pers__info"]}>
            <input
              type="text"
              placeholder="Name"
              id="name"
              value={name.value}
              onChange={(e) => name.onChange(e)}
              onBlur={(e) => name.onBlur(e)}
            />
            <input
              type="text"
              placeholder="Surname"
              value={surname.value}
              onChange={(e) => surname.onChange(e)}
              onBlur={(e) => surname.onBlur(e)}
            />
          </div>
        </div>
        <div className={styles["second-section__wrap"]}>
          <div className={styles["workerks-pers__wrap"]}>
            <label htmlFor="">Gender</label>
            <div className={styles["worker-pers__info"]}>
              <select name="" id="">
                <option value="">Male</option>
                <option value="">Female</option>
              </select>
            </div>
          </div>
          <div className={styles["workerks-pers__wrap"]}>
            <label htmlFor="">Birthday</label>
            <div className={styles["worker-pers__info"]}>
              <input
                type="date"
                onChange={(e) => birthDay.onChange(e)}
                onBlur={(e) => birthDay.onBlur(e)}
              />
            </div>
          </div>
        </div>
        <div className={styles["workerks-pers__wrap"]}>
          <label htmlFor="">Operational data</label>
          <div className={styles["worker-pers__info"]}>
            <input
              type="number"
              name=""
              id=""
              placeholder="Salary"
              min="300"
              onChange={(e) => salary.onChange(e)}
              onBlur={(e) => salary.onBlur(e)}
            />
            <input
              type="text"
              placeholder="Department"
              onChange={(e) => department.onChange(e)}
              onBlur={(e) => department.onBlur(e)}
            />
          </div>
        </div>
        <button type="submit" onClick={(e) => sumbitHandler(e)}>
          Add
        </button>
      </form>
    </>
  );
};
export default AddWorkerForm;
