import { useState } from "react";
import { Worker } from "../Worker/Worker";
import { ListHeader } from "../ListHeader/ListHeader";
import { Tabs } from "../Tabs/Tabs";
import { Search } from "../SearchField/Search";
import styles from "./WorkersList.module.css";

const WorkersList = (props) => {
  const workeksList = props.list;
  let listContent;
  let estimates;

  let totalAmount = workeksList.length;

  let totalSalaries = workeksList.reduce((total, worker) => {
    return Math.round((total + parseInt(worker.salary)) / workeksList.length);
  }, 0);

  estimates = (
    <div className={styles.estimates}>
      <div>{`Average salary: ${totalSalaries}$`}</div>
      <div>{`Quantity: ${totalAmount}`}</div>
    </div>
  );

  listContent = (
    <>
      {workeksList.map((worker) => (
        <li key={worker.id}>
          <Worker
            name={worker.name}
            surname={worker.surname}
            gender={worker.gender}
            birthday={worker.birthDay}
            salary={worker.salary}
            department={worker.department}
          />
        </li>
      ))}
      <>{totalAmount && totalSalaries > 0 ? estimates : <p>No info!</p>}</>
    </>
  );

  return (
    <div className={styles["list__wrap"]}>
      <div className={styles["content-wrap"]}>
      
        <ListHeader />
        {listContent.props.children.length === 0 ? (
          <p>No workers FUCK!</p>
        ) : (
          listContent
        )}
      </div>
    </div>
  );
};

export default WorkersList;
