import React, { useState } from "react";
import WorkersList from "./components/WorkersList";
import AddWorkerForm from "./components/AddWorkerForm";

const App = () => {
  const [workers, setWorkers] = useState([]);
  const expenses = [
    {
      id: "e1",
      name: "Alex",
      surname: "Ziierov",
      sex: "male",
      birthdayDate: new Date(1999, 11, 5),
      salary: "25000",
      department: "IOS dev",
    },
    {
      id: "e2",
      name: "Anstasia",
      surname: "Kovalenko",
      sex: "female",
      birthdayDate: new Date(2003, 7, 1),
      salary: "32000",
      department: "IOS dev",
    },
  ];
  const submitHandler = (obj) => {
    setWorkers((prevList) => {
      let updatedList = [...prevList];
      updatedList.concat({
        name: obj.name,
        surname: obj.surname,
        gender: obj.gender,
        birthday: obj.birthDay,
        salary: obj.salary,
        department: obj.department,
        id: Math.random().toString(),
      });
    });
  };

  return (
    <>
      <AddWorkerForm onSumbitHandler={submitHandler} />
      <WorkersList list={expenses} />
    </>
  );
};

export default App;
