import React, { useState } from "react";
import { Worker } from "./Worker";
import styles from "./WorkersList.module.css";
import { ListHeader } from "./ListHeader";
import { FilterButtons } from "./FilterButtons";

const WorkersList = (props) => {
  const [filterButtonSelected, setFilterButtonSelected] = useState(false);
  const clickHandler = (e) => {
    console.log(filterButtonSelected);
    setFilterButtonSelected((prevState) => !prevState);
  };
  return (
    <ul>
      <div className={styles["list__wrap"]}>
        <FilterButtons
          text={"All"}
          onClick={clickHandler}
          onSelect={filterButtonSelected}
        />
        <FilterButtons
          text={"IOS devs"}
          onClick={clickHandler}
          onSelect={filterButtonSelected}
        />
        <FilterButtons
          text={"Des"}
          onClick={clickHandler}
          onSelect={filterButtonSelected}
        />
        <div className={styles["content-wrap"]}>
          <ListHeader />
          {props.list.map((worker) => (
            <li key={worker.id}>
              <Worker
                name={worker.name}
                surname={worker.surname}
                gender={worker.gender}
                birthday={worker.birthDay}
                salary={worker.salary}
                department={worker.department}
              />
            </li>
          ))}
        </div>
      </div>
    </ul>
  );
};

export default WorkersList;
