import React, { useState } from "react";
import { Worker } from "./Worker";
import styles from "./WorkersList.module.css";
import { ListHeader } from "./ListHeader";
import { Tabs } from "./Tabs/Tabs";

const WorkersList = (props) => {
  const [tabIndexClicked, setTabIndexClicked] = useState(0);
  const setTabIndex = (index) => {
    setTabIndexClicked(index);
  };
  let listContent = (
    <>
      {props.list.map((worker) => (
        <li key={worker.id}>
          <Worker
            name={worker.name}
            surname={worker.surname}
            gender={worker.gender}
            birthday={worker.birthDay}
            salary={worker.salary}
            department={worker.department}
          />
        </li>
      ))}
    </>
  );

  return (
    <ul>
      <div className={styles["list__wrap"]}>
        <div className={styles["content-wrap"]}>
          <Tabs onTabIndexHandler={setTabIndex} tabIndex={tabIndexClicked} />
          <ListHeader />
          {listContent}
        </div>
      </div>
    </ul>
  );
};

export default WorkersList;
